// Function node (NOT Function Item)

// stable hash
function hashKey(title, company, url) {
  const s = (title || '') + '|' + (company || '') + '|' + (url || '');
  let h = 5381; 
  for (let i = 0; i < s.length; i++) h = ((h << 5) + h) + s.charCodeAt(i);
  return (h >>> 0).toString(16);
}

// ban list
const BAN = [
  /student\s*(assistant|helper)?/i,
  /\bstudent\b/i,
  /studenter/i,
  /\bpost[- ]?doc(toral)?\b/i,
  /\bpraktikant\b/i,
];

const input = $input.all();
const seen = new Set();
const out = [];

for (let i = 0; i < input.length; i++) {
  const src = input[i];
  const j = { ...(src.json || {}) }; // shallow clone

  // ban check
  const hay = ((j.title || '') + ' ' + (j.raw_description || '')).toLowerCase();
  if (BAN.some(re => re.test(hay))) continue;

  // dedupe
// ‰ΩøÁî® Google Jobs ID ‰Ωú‰∏∫‰∏ªË¶ÅÂéªÈáçÈîÆÔºåÂ¶ÇÊûúÊ≤°ÊúâÂàôÂõûÈÄÄÂà∞ title+company
const k = j.id ? j.id : hashKey(
  (j.title || '').toLowerCase(),
  (j.company || '').toLowerCase(),
  '' // ÂøΩÁï• URLÔºåÂõ†‰∏∫ÂÆÉÂèØËÉΩÂèòÂä®
);
  if (seen.has(k)) continue;
  seen.add(k);

  j.hash = k;

  // preserve pairing so downstream nodes don't complain
  out.push({
    json: j,
    pairedItem: { item: i }
  });
}

return out;

// === Prepare compact prompt input for the AI scoring agent ===

// Grab the fit criteria from the earlier node
const fit_crit = $('Fit Criteria').first().json;

// Fetch your CV text from the earlier node
// (Change 'MapResume' to your actual node name that contains cv_text)
const cv_text = $('MapCV').item.json.cv_text;

const ps_text = $('MapPS').item.json.ps_text.slice(0, 5000); // safety cap

const job = $json;

// limit long job text to avoid token waste
const maxDesc = 5000; // ~1000 words
const cleanDesc = (job.raw_description || '')
  .replace(/\s+/g, ' ')
  .trim()
  .slice(0, maxDesc);

// build a structured summary text block for LLM
const jobTextBlock = [
  `Job title: ${job.title || ''}`,
  `Company: ${job.company || ''}`,
  job.location ? `Location: ${job.location}` : '',
  job.seniority_level ? `Seniority: ${job.seniority_level}` : '',
  job.employment_type ? `Employment type: ${job.employment_type}` : '',
  job.industries ? `Industry: ${job.industries}` : '',
  job.job_function ? `Function: ${job.job_function}` : '',
  '',
  'Job Description:',
  cleanDesc,
].filter(Boolean).join('\n');

// return one item per job
return {
  job_text: jobTextBlock,
  cv_text: cv_text.slice(0, 10000), // safety cap
  job_meta: {
    title: job.title || '',
    company: job.company || '',
    url: job.url || job.apply_url || '',
    hash: job.hash || '',
    location: job.location || '',
    posted_at: job.posted_at || '',
    source: job.source || 'LinkedIn'
  },
  ps_text: ps_text,
  fit_criteria: {
    background_match_high: fit_crit.background_match_high || 'explicitly mentions NLP or identical field',
    background_match_mid: fit_crit.background_match_mid || 'data consultant roles',
    background_match_low: fit_crit.background_match_low || 'unrelated domain',
    skills_overlap: fit_crit.skills_overlap || 'Compare coding, analysis, statistics, data tools, and soft skills appearing in both CV and job text',
    seniority_high: fit_crit.seniority_high || 'junior',
    seniority_mid: fit_crit.seniority_mid || 'senior/confirmed',
    seniority_low: fit_crit.seniority_low || 'manager/executive',
    language_requirement_high: fit_crit.language_requirement_high || 'English-friendly or international',
    language_requirement_mid: fit_crit.language_requirement_mid || 'requires fluent ' + '',
    language_requirement_low: fit_crit.language_requirement_low || 'requires fluency in languages other than ' + '',
    company_score_high: fit_crit.company_score_high || 'large or international IT/AI/NLP/Data company',
    company_score_mid: fit_crit.company_score_mid || 'medium data-driven company',
    company_score_low: fit_crit.company_score_low || 'unrelated small local firm or startup'
  }
};

// Function Item version ‚Äì returns ONE object

let raw;

if (typeof $json.output === 'string') {
  raw = $json.output;
} else if ($json.choices?.[0]?.message?.content) {
  raw = $json.choices[0].message.content;
} else {
  raw = JSON.stringify($json);
}

raw = raw.trim();
raw = raw.replace(/^```json/i, '').replace(/```$/, '').trim();

let data;
try {
  data = JSON.parse(raw);
} catch (e) {
  const m = raw.match(/\{[\s\S]*\}/);
  if (m) {
    try { data = JSON.parse(m[0]); }
    catch (e2) { data = { parse_error: e2.message, raw }; }
  } else {
    data = { parse_error: e.message, raw };
  }
}

// return a single object (Function Item requires an object)
return data;

// Keep first 15 highest-scoring items (already sorted)
const items = $input.all();
const top = items.slice(0, 15);
return top; // they are already {json: ...} items

// === Beautiful, consistent HTML for Top jobs (dark theme, robust to nested fields) ===
function esc(s){return String(s??'').replace(/[<&>]/g,m=>({'<':'&lt;','>':'&gt;','&':'&amp;'}[m]));}

// Accept {json:{...}} or plain {...}
const raws = $input.all();
const items = raws.map(it => (it && typeof it==='object' && 'json' in it) ? it.json : it);

// Helpers
const titleCase = s => String(s||'')
  .toLowerCase()
  .replace(/\b([a-z])/g, m => m.toUpperCase());

function pick(i){
  const out  = i.output   || {};
  const meta = i.job_meta || {};
  const keywords = Array.isArray(out.keywords) ? out.keywords : [];
  const bullets  = Array.isArray(out.fit_bullets) ? out.fit_bullets.slice(0,3) : [];
  const overall  = (out.score && out.score.overall!=null) ? out.score.overall : '';
  return {
    title: meta.title || '',
    company: meta.company || '',
    location: meta.location || '',
    url: meta.url || i.url || i.apply_url || '#',
    score: overall,
    summary: out.summary || '',
    bullets,
    connector: out.connector || '',
    chips: keywords.slice(0,6).map(titleCase)
  };
}

function scoreColor(n){
  if (n==='' || isNaN(n)) return '#9aa0a6';
  n = Number(n);
  if (n >= 85) return '#22c55e'; // green
  if (n >= 70) return '#3b82f6'; // blue
  if (n >= 55) return '#eab308'; // amber
  return '#f97316';              // orange
}

const style = `
