`;

function card(item){
  const j = pick(item);
  const sc = j.score === '' ? '‚Äî' : String(j.score);
  const scColor = scoreColor(j.score);
  const chips = j.chips.length ? `<div class="chips">${j.chips.map(c=>`<span class="chip">${esc(c)}</span>`).join('')}</div>` : '';
  const bullets = j.bullets.length ? `<ul>${j.bullets.map(b=>`<li>${esc(b)}`).join('')}</ul>` : '';
  const connector = j.connector ? `<div class="connector">${esc(j.connector)}</div>` : '';
  return `
    <div class="card">
      <div class="row" style="justify-content:space-between">
        <h3 class="title">${esc(j.title)}</h3>
        <span class="score-pill" style="border-color:${scColor};color:${scColor}">Score: ${sc}/100</span>
      </div>
      <div class="meta">${esc(j.company)} ‚Äî ${esc(j.location)}</div>
      ${chips}
      <div class="summary">${esc(j.summary)}</div>
      ${bullets}
      ${connector}
      <div class="divider"></div>
      <a class="btn" href="${esc(j.url)}" target="_blank">View & Apply</a>
    </div>
  `;
}

const html = `
<!doctype html>
<html>
<head><meta charset="utf-8"><style>${style}</style></head>
<body>
  <div class="wrap">
    <h2>üî• Your Top ${items.length} Job Matches Today</h2>
    ${items.map(card).join('\n')}
    <div class="footer">Sent by your Job Agent at ${new Date().toLocaleString()}.</div>
  </div>
</body>
</html>
`;

// Return correct shape for your node type
const first = $input.first();
const isFunctionNode = first && typeof first==='object' && 'json' in first;
return isFunctionNode ? [{ json: { html } }] : { html };
